INSERT INTO `faq_content` VALUES ('1019', '&lt;p&gt;\r\n	&lt;span style=&quot;color:#E53333;&quot;&gt;合成要求：&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;color:#E53333;&quot;&gt;&lt;span style=&quot;color:#000000;&quot;&gt;1、人物等级到达&lt;/span&gt;&lt;span style=&quot;color:#337FE5;&quot;&gt;55级&lt;/span&gt;&lt;span style=&quot;color:#000000;&quot;&gt;才能合宠&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;color:#E53333;&quot;&gt;&lt;span style=&quot;color:#000000;&quot;&gt;2、&lt;span style=&quot;color:#337FE5;&quot;&gt;野生宠物&lt;/span&gt;不能合成&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;color:#E53333;&quot;&gt;&lt;span style=&quot;color:#000000;&quot;&gt;3、合成的宠物&lt;span style=&quot;color:#337FE5;&quot;&gt;无等级限制&lt;/span&gt;（0级宠物就可用来直接合成）&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;color:#E53333;&quot;&gt;&lt;span style=&quot;color:#000000;&quot;&gt;4、野生宠物可以使用&lt;span style=&quot;color:#009900;&quot;&gt;【#####339002】&lt;/span&gt;洗炼成宝宝后再合成&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;color:#E53333;&quot;&gt;&lt;span style=&quot;color:#E53333;&quot;&gt;过程演示：&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;color:#E53333;&quot;&gt;&lt;span style=&quot;color:#E53333;&quot;&gt;&lt;span style=&quot;color:#000000;&quot;&gt;消耗两个宠物，合成一个新的宠物：&lt;/span&gt;&lt;span style=&quot;color:#337FE5;&quot;&gt;宠物A+宠物B=宠物C&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;color:#E53333;&quot;&gt;&lt;span style=&quot;color:#E53333;&quot;&gt;&lt;span style=&quot;color:#337FE5;&quot;&gt;&lt;span style=&quot;color:#E53333;&quot;&gt;打开&lt;/span&gt;&lt;span style=&quot;color:#009900;&quot;&gt;：【##16】（&lt;span style=&quot;color:#000000;&quot;&gt;点击跳转）&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;color:#E53333;&quot;&gt;&lt;span style=&quot;color:#E53333;&quot;&gt;&lt;span style=&quot;color:#E53333;&quot;&gt;&lt;span style=&quot;color:#E53333;&quot;&gt;&lt;span style=&quot;color:#E53333;&quot;&gt;合成结果：&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;color:#E53333;&quot;&gt;&lt;span style=&quot;color:#E53333;&quot;&gt;&lt;span style=&quot;color:#337FE5;&quot;&gt;&lt;span style=&quot;color:#009900;&quot;&gt;&lt;span style=&quot;color:#000000;&quot;&gt;1、&lt;/span&gt;&lt;span style=&quot;color:#337FE5;&quot;&gt;造型&lt;/span&gt;&lt;span style=&quot;color:#000000;background-color:#FFFFFF;&quot;&gt;为原宠物二选一&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;color:#E53333;&quot;&gt;&lt;span style=&quot;color:#E53333;&quot;&gt;&lt;span style=&quot;color:#337FE5;&quot;&gt;&lt;span style=&quot;color:#009900;&quot;&gt;&lt;span style=&quot;color:#000000;&quot;&gt;2、&lt;/span&gt;&lt;span style=&quot;color:#337FE5;&quot;&gt;等级、技能&lt;/span&gt;&lt;span style=&quot;color:#000000;&quot;&gt;为原宠物之间随机&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;color:#E53333;&quot;&gt;&lt;span style=&quot;color:#E53333;&quot;&gt;&lt;span style=&quot;color:#337FE5;&quot;&gt;&lt;span style=&quot;color:#009900;&quot;&gt;&lt;span style=&quot;color:#000000;&quot;&gt;3、&lt;/span&gt;&lt;span style=&quot;color:#337FE5;&quot;&gt;成长、资质&lt;/span&gt;&lt;span style=&quot;color:#000000;&quot;&gt;有可能高/低于原宠物&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;color:#E53333;&quot;&gt;&lt;span style=&quot;color:#E53333;&quot;&gt;&lt;span style=&quot;color:#337FE5;&quot;&gt;&lt;span style=&quot;color:#009900;&quot;&gt;&lt;span style=&quot;color:#E53333;&quot;&gt;您可能感兴趣&lt;/span&gt;：【###合宠限制】【###合宠预览】&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/